import { Card, CardHeader, CardActions, Button } from "@mui/material";
import { useLocation } from "react-router-dom";
import { useTheme } from "@mui/material/styles";
import { useNavigate } from "react-router-dom";
import { routes } from "./../App/App";
function Shell({ heading, content, subheading, index }) {
  const theme = useTheme();
  const navigate = useNavigate();
  const location = useLocation();

  const properties = (theme) => ({
    container: {
      component: "div",
    },
    card: {
      component: "div",
      variant: "outlined",
    },
    cardHeader: {
      titleTypographyProps: {
        component: "h3",
      },
      subheaderTypographyProps: {
        variant: "h4",
        component: "h4",
      },
    },
  });

  const styles = (theme) => ({
    card: {
      p: 1,
      height: "60%",
      width: "95%",
      display: "flex",
      flexDirection: "column",
      boxSizing: "border-box",
      alignItems: "center",
      justifyContent: "center",
      [theme.breakpoints.up("sm")]: {
        width: "85%",
      },
      [theme.breakpoints.up("md")]: {
        p: 5,
        width: "80%",
      },
      [theme.breakpoints.up("lg")]: {
        width: "60%",
      },
    },
    cardHeader: {
      h3: {
        fontSize: "1.2rem",
        fontWeight: "bold",
      },
      [theme.breakpoints.up("md")]: {
        h3: {
          fontSize: "2rem",
        },
      },
    },
  });

  return (
    <Card sx={styles(theme).card} {...properties(theme).card}>
      <CardHeader
        sx={styles(theme).cardHeader}
        {...properties(theme).cardHeader}
        title={heading}
        subheader={subheading}
      ></CardHeader>
      {content}
      <CardActions>
        {location.pathname === "/intro" || location.pathname === "/" ? (
          ""
        ) : (
          <Button
            onClick={() => navigate(routes[index - 1].path)}
            variant="outlined"
            size="medium"
            color="secondary"
          >
            Back
          </Button>
        )}
        <Button
          onClick={() => navigate(routes[index + 1].path)}
          variant="outlined"
          size="medium"
          color="primary"
        >
          Next
        </Button>
      </CardActions>
    </Card>
  );
}

export default Shell;
