import { Card, CardHeader, Pagination } from "@mui/material";
import { blue } from "@mui/material/colors";
// import { theme } from "../../App/App";
// import "./styles.css";
// import MediaQuery from "react-responsive";
import { useTheme } from "@mui/material/styles";
function Shell({ heading, content, subheading }) {
  //   const theme = useTheme();

  //   const mobile = useMediaQuery(theme.breakpoints.up("xs"));

  //   console.log(useMediaQuery(theme.breakpoints.up("xs")));

  //   h1 {
  //   color: blue;
  // }

  // .MuiContainer-root {
  //   height: inherit;
  //   background-color: #ecf0f3;
  //   box-sizing: border-box;
  //   display: flex;
  //   align-items: center;
  //   justify-content: center;
  // }

  // .MuiCard-root {
  //   height: 50%;
  //   width: 80%;
  //   background-color: #ecf0f3;
  //   box-sizing: border-box;
  //   display: flex;
  //   align-items: center;
  //   justify-content: center;
  //   flex-direction: column;
  // }

  // /* Small devices (portrait tablets and large phones, 600px and up) */
  // @media only screen and (min-width: 600px) {
  //   .MuiCard-root {
  //     height: 50%;
  //     width: 70%;
  //   }
  // }

  const theme = useTheme();

  const properties = (theme) => ({
    container: {
      component: "div",
    },
    card: {
      component: "div",
      variant: "outlined",
    },
    cardHeader: {
      titleTypographyProps: {
        component: "h3",
      },
      subheaderTypographyProps: {
        variant: "h4",
        component: "h4",
      },
    },
  });

  const styles = (theme) => ({
    card: {
      p: 1,
      height: "60%",
      width: "95%",
      display: "flex",
      flexDirection: "column",
      boxSizing: "border-box",
      alignItems: "center",
      justifyContent: "center",
      [theme.breakpoints.up("sm")]: {
        backgroundColor: "green",
        width: "85%",
      },
      [theme.breakpoints.up("md")]: {
        p: 5,
        backgroundColor: "blue",
        width: "80%",
      },
      [theme.breakpoints.up("lg")]: {
        backgroundColor: "yellow",
        width: "60%",
      },
    },
    cardHeader: {
      h3: {
        fontSize: "1.2rem",
        fontWeight: "bold",
      },
      [theme.breakpoints.up("md")]: {
        h3: {
          fontSize: "2rem",
        },
      },
    },
  });

  return (
    <Card sx={styles(theme).card} {...properties(theme).card}>
      <CardHeader
        sx={styles(theme).cardHeader}
        {...properties(theme).cardHeader}
        title={heading}
        subheader={subheading}
      ></CardHeader>
      {content}
    </Card>
  );
}

export default Shell;
