  //   align-items: center;
  //   justify-content: center;
  //   flex-direction: column;
  // }

  // /* Small devices (portrait tablets and large phones, 600px and up) */
  // @media only screen and (min-width: 600px) {
  //   .MuiCard-root {
  //     height: 50%;
  //     width: 70%;
  //   }
  // }

  const properties = {
    container: {
      component: "div",
    },
    card: {
      component: "div",
      variant: "outlined",
    },
    cardHeader: {
      titleTypographyProps: {
        variant: "",
        component: "h4",
        align: "center",
        gutterBottom: true,

        // gutterTop: true,
      },
      subheaderTypographyProps: {
        variant: "h6",

        component: "h6",
        align: "center",
      },
    },
  };

  const styles = {
    card: { p: 5, height: 1 / 2, width: "80%" },
    content: {
      height: "100%",
    },
  };

  return (
    <Card sx={styles.card} {...properties.card}>
      <CardHeader
        {...properties.cardHeader}
        title={heading}
        subheader={subheading}
      ></CardHeader>
      <CardContent sx={styles.content}>{content}</CardContent>
    </Card>
  );
}

export default Shell;
