import {
  Card,
  CardContent,
  useTheme,
  CardHeader,
  useMediaQuery,
} from "@mui/material";
import "./styles.css";
import MediaQuery from "react-responsive";

function Shell({ heading, content, subheading }) {
  const styles = {
    container: {
      component: "div",
    },
    card: {
      component: "div",
      variant: "outlined",
    },
    cardHeader: {
      titleTypographyProps: {
        variant: theme.breakpoints.mobile ? "h4" : "h2",
        component: "h4",
        align: "center",
        gutterBottom: true,

        // gutterTop: true,
      },
      subheaderTypographyProps: {
        variant: "h6",

        component: "h6",
        align: "center",
      },
    },
  };

  const theme = useTheme();

  //   const desktop = useMediaQuery(theme.breakpoints.up("lg"));
  //   const tablet = useMediaQuery(theme.breakpoints.up("sm"));
  const mobile = useMediaQuery(theme.breakpoints.up("xs"));

  switch (window.theme.breakpoints) {
    case mobile:
      return "h5";
    default:
      return "h4";
  }

  return (
    <Card {...styles.card}>
      <CardHeader
        {...styles.cardHeader}
        title={heading}
        subheader={subheading}
      ></CardHeader>
      <CardContent>{content}</CardContent>
    </Card>
  );
}

// function sizeHeader() {

//     switch(window.theme.breakpoints) {
//         case desktop:
//             return
//     }
// }

export default Shell;
